        console.log(`${name} was clicked`)
    } 
    return(
        <main>
            <p>Hello {handleNameChange()}</p>
            <button onClick={handleClick}>Click me</button>
            <button onClick={() =>handleClick2("dave")}>Clicked</button>

        </main>
    )
}
